package com.ssm.po;

public class Today {
	private int id;
	private int aid;
	private String title;
	public int getId() {
		return id;
	}
	public void setId(int id) {
		this.id = id;
	}
	public int getAid() {
		return aid;
	}
	public void setAid(int aid) {
		this.aid = aid;
	}
	public String getTitle() {
		return title;
	}
	public void setTitle(String title) {
		this.title = title;
	}
	public Today() {
		super();
		// TODO Auto-generated constructor stub
	}
	@Override
	public String toString() {
		return "Today [id=" + id + ", aid=" + aid + ", title=" + title + "]";
	}
	
	
}
